(function(){let e=$("#friend-button");e.click((function(t){t.preventDefault(),$.ajax({type:"post",url:e.attr("href")}).done((function(t){console.log(t),t.data.friend?(e.text("Remove Friend"),e.attr("href",`/users/removefriend/?friendshipId=${t.data.friendshipId}&profileUser=${t.data.toUser}`)):(e.text("Add Friend"),e.attr("href",`/users/makefriend/?toUser=${t.data.toUser}`))})).fail((function(e){console.log("error in completing the request",e)}))}))})();